:root {
  --primary: #2c3e50;
  --secondary: #ecf0f1;
  --text: #333;
  --bg: #fff;
  --transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

body.dark {
  --primary: #34495e;
  --secondary: #2c3e50;
  --text: #ecf0f1;
  --bg: #1a1a1a;
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background-color: var(--bg);
  color: var(--text);
  font-family: Arial, sans-serif;
  transition: var(--transition);
  padding: 1rem;
}

.hero-container {
  position: relative;
  text-align: center;
}

.hero {
  display: inline-block;
  padding: 2.5rem 5rem;
  border-radius: 16px;
  background-color: var(--secondary);
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.08);
  transition: var(--transition);
}

.hero:hover {
  transform: translateY(-6px);
  box-shadow: 0 12px 40px rgba(0, 0, 0, 0.12);
}

h1 {
  font-size: clamp(2.5rem, 10vw, 6rem);
  color: var(--primary);
  line-height: 1.2;
  transition: var(--transition);
}

.theme-toggle {
  position: absolute;
  top: -3rem;
  right: 0;
  background: none;
  border: none;
  color: var(--text);
  font-size: 1.5rem;
  cursor: pointer;
  padding: 0.5rem;
  border-radius: 50%;
  transition: var(--transition);
}

.theme-toggle:hover {
  background-color: var(--secondary);
  transform: scale(1.1);
}

@media (max-width: 768px) {
  .hero {
    padding: 1.5rem 3rem;
  }

  h1 {
    font-size: clamp(1.8rem, 8vw, 4rem);
  }

  .theme-toggle {
    top: -2rem;
    font-size: 1.2rem;
  }
}

@media (prefers-color-scheme: dark) {
  body:not(.light) {
    --primary: #34495e;
    --secondary: #2c3e50;
    --text: #ecf0f1;
    --bg: #1a1a1a;
  }
}

